import React  from "react";
import ReactDOM from "react-dom";
import GameCircle from "./GameCircle";
import '../Game.css'

const NO_PLAYER = 0;
const PLAYER_1 = 1;
const PLAYER_2 = 2;
const CIRCLE_NUM =16;

const GameBoard = () => {
   const [gameState, setGameState] = React.useState(Array(16).fill(0));
   const [currentPlayer, setCurrentPlayer] = React.useState(PLAYER_1);

   //console.log("GameBoard", gameState);
   const initBoard = () => {
      const circles = [];
      for (let i = 0; i < CIRCLE_NUM; i++) {
         const circle = renderCircle(i);
         circles.push(circle);
      }
      return circles;
   }

   const handleClick = (id) => {
      //console.log("Clicked" + id);
      // const newGameState = [...gameState]; // copy of the array of game state
      // newGameState[id] = currentPlayer; 
      // setGameState(newGameState);
      setGameState((gameState) => {return gameState.map((value, index) => {
         if (index === id) {return currentPlayer;}
         return value;}
      )});

      setCurrentPlayer(currentPlayer === PLAYER_1 ? PLAYER_2 : PLAYER_1); // primitive value, so no need to copy
   }

   const renderCircle = (id) => {
      return <GameCircle id={id} className={`player_${gameState[id]}`} onCircleClicked={handleClick}>

            </GameCircle>
   }

    return(
         <div className="gameBoard">
            {initBoard()}
         </div>
    );
 }

 export default GameBoard;
