            </GameCircle>
            <GameCircle id={9} onCircleClicked={handleClick}>

            </GameCircle>
            <GameCircle id={10} onCircleClicked={handleClick}>

            </GameCircle>
            <GameCircle id={11} onCircleClicked={handleClick}>

            </GameCircle>    
            <GameCircle id={12} onCircleClicked={handleClick}>

            </GameCircle>
            <GameCircle id={13} onCircleClicked={handleClick}>

            </GameCircle>
            <GameCircle id={14} onCircleClicked={handleClick}>

            </GameCircle>
            <GameCircle id={15} onCircleClicked={handleClick}>

            </GameCircle>
         </div>
    );
 }

 export default GameBoard;
