export const CartReducer = (state, action) => {
    debugger;
    switch (action.type) {
        case 'ADD_TO_CART':
            const index = state.cartItems.findIndex(p => p.id === action.payload.id);

            if (index === -1) {
                state.cartItems.push({...action.payload, quantity: 1});
            }else{
                state.cartItems[index].quantity++;
            }
            return state;

        case 'REMOVE_FROM_CART':
            return state;
        default:
            return state;
    }
}
