import React ,{useState} from 'react'
import { fetchProductById } from '../fetcher'
import { useParams } from 'react-router-dom'
import { styled } from 'styled-components';


const ProductDetail = () => {
    const {productId} = useParams();
    const [product, setProduct] = useState({errorMessage: '', data: {}});
    React.useEffect(() => {
        const fetchData = async () => {
            const productObject = await fetchProductById(productId);
            setProduct(productObject);
        }
        fetchData();
    },[productId]);

  return (
    <article>
    <div className="category-product-title">
        {product.data.title}
    </div>

    <figure>
        <div className="category-product-image-container">
            <img src={`/assets/${product.data.image}`} alt={product.data.title} />
        </div>
    </figure>

    <aside>
        {/* <div className="category-product-info-dimensions">
            <h3>Dimensions</h3>
            <label>{product.data.specs.dimensions}</label>
        </div> */}

        {/* { product.data.specs.capacity &&
            <div className="category-product-info-capacity">
                <h3>Capacity</h3>
                <label>{product.data.specs.capacity}</label>
            </div>
        } */}

        <div className="category-product-info-features">
            <h3>Features</h3>
            <ul>
                {product.data.features?.map((f,i) => {
                    return <li key={`feature${i}`}>{f}</li>
                })}
            </ul>
        </div>
    </aside>

    <aside className='category-product-finance'>
        <div className="category-product-finance-price">
            &pound;{product.data.price}
        </div>
        <div className="category-product-info-stock">
            <label>stock level: {product.data.stock}</label>
            <label>free delivery</label>
        </div>
        <div className="category-product-action">
            <button>add to cart</button>
        </div>
    </aside> 
    <ProductDescription>
        {product.data.description}
    </ProductDescription>

</article>
  )
}

export default ProductDetail

const ProductDescription = styled.div`
    grid-column: 1 / span 3;
`;
